-- ----------------------------
-- Table structure for `t_faq`
-- ----------------------------
DROP TABLE IF EXISTS `t_faq`;
CREATE TABLE `t_faq` (
  `id` int(255) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL,
  `content` varchar(1000) DEFAULT NULL,
  `time` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of t_faq
-- ----------------------------
INSERT INTO `t_faq` VALUES ('1', '什么是Synergy？', 'Synergy是利用区块链、物联网和大数据技术建立的去中心化、公开透明、高效便捷的国际物流数据交互平台以及供应链金融服务平台。旨在为国际物流、国际贸易领域相关的个人、企业和政府机构提供物流跟踪、信息查询、跨境支付、供应链融资等综合服务。', '4232423423423');
INSERT INTO `t_faq` VALUES ('3', 'Synergy的核心优势有哪些？', 'Synergy的核心优势在于以下几个方面：\n1、科学的架构：基础公链+智能合约的科学架构，同时解决物流+信息流+资金流三大问题\n2、合理的机制：合理的“错位激励”机制，引导个人、企业和政府机构参与平台建设\n3、厚积薄发的资源：二十余年跨境供应链的经验，多家国际货代企业联盟的支持，几十个国家和地区7万余外贸客户的资源\n4、专业的团队：专业化的团队。区块链技术团队与国际供应链团队的强强组合。', '1532423837');
INSERT INTO `t_faq` VALUES ('4', 'Synergy代币的价值体现有哪些？', '为企业数据上链提供很好的token激励机制，能使供应链健康快速的发展。利用token作为通证授信融资，能为一些中小资企业解决资金流的问题，可利用代币参与跨境结算。', '1532423880');
INSERT INTO `t_faq` VALUES ('5', 'Synergy国际物流数据查询平台的搭建进程是怎样的？未来会形成什么样的规模？', '以强大的实体国际物流公司为支撑，通过已有的数据，快速整合到Synergy国际物流查询平台中。与此同时，利用token激励其他货代公司上传数据，进一步完善一体化的区块链国际物流数据查询平台。\n未来将会把所有的国际物流信息全部整合到Synergy平台上，利用区块链查询各个环节的信息，实现智能化跟踪与查询。', '1532424044');
INSERT INTO `t_faq` VALUES ('6', 'Synergy项目为何选择供应链行业作为切入点？', '供应链是一条庞大，繁琐，冗长且具有市场前景的一套体系，传统供应链存在的问题也较多。利用Synergy的原生代币能够使各个环节更方便的联通在一起，区块链网络能够公正透明的解决行业痛点，能够更好的服务供应链金融，使供应链迅速整合在一条完整高效的链条体系上，更便于经济流通。', '1532579182');
INSERT INTO `t_faq` VALUES ('7', '当Synergy应用越来越广泛的时候，Synergy怎么处理潜在的巨大交易负荷，Synergy的可拓展性怎么样？', ' 关于主链处理交易负荷的能力，和以太坊对比，其目前采用PoW共计机制，平均出块时间在30S左右，平均每块交易数在几十到几百量级，一天处理的交易数最多已接近50万，已经能满足公有链应用场景下的大部分需求；而Synergy主链采用PoST+PoW 共识机制，处理负荷能做到更大，能在承载更大规模的交易负荷，另一方面，Synergy采用双层结构设计，交易数据与物流数据分层管理。Synergy这种方式本身设计目标就是针对的可扩展的且规模较大的生态系统而言，所以Synergy应对规模的扩大是很具备扩展性的。', '1532579190');
INSERT INTO `t_faq` VALUES ('8', 'Synergy计划如何解决供应链金融领域的融资问题，怎么保障资金的安全？', '   供应链中最重要的是提单，企业申请融资时需提单质押，货物的物权归属Synergy放款方。整个交易结束后释放提单。通过物权的归宿，保证了资金的安全。', '1532579196');
INSERT INTO `t_faq` VALUES ('9', '随着Synergy在全球的认可程度不断增加，Synergy通过什么策略加强和支持者的交流？', '    首先，做好Synergy品牌管理，专业解答Synergy发展进程中需要反馈的各类问题并定期交流汇报。其次Synergy团队积极参与各项组织活动，定期和媒体见面交流。\n ', '1532579205');

